import React from 'react'
import { useState } from 'react'
import './Modal.css'

function Modal({ text, isVisible }) {
  const [close, setClose] = useState(isVisible)

  function handleClose() {
    setClose(!isVisible)
  }

  return !close ? null : (
    <div className='modal' onClick={handleClose}>
      <h1>Result window</h1>
      <p>{text}</p>
    </div>
  )
}

export default Modal
