import { useState } from 'react'
import Modal from './Modal'
import './Login.css'

function Login() {
  const [data, setData] = useState({ username: '', password: '' })
  const [resultData, setResultData] = useState(null)
  const [isModal, setIsModal] = useState(false)
  const [isValid, setIsValid] = useState(false)
  const [varification, setVarification] = useState({
    username: null,
    password: null,
  })

  function handleFromSubmit(event) {
    event.preventDefault()

    fetch('http://localhost:3000/ylab', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data),
    })
      .then((result) => result.json())
      .then((result) => {
        setResultData({ ...resultData, message: result })
        setIsModal(true)
      })
      .catch((error) => {
        setResultData({ ...resultData, error })
        setIsModal(true)
      })
  }

  function handleInputChange(e, name) {
    setData({ ...data, [name]: e.target.value })
  }

  function handleValid(e, name) {
    switch (name) {
      case 'username':
        let re =
          /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/

        if (re.test(e.target.value)) {
          setVarification({ ...varification, username: null })

          !varification.password ? setIsValid(false) : setIsValid(true)
        } else {
          setIsValid(false)
          setVarification({ ...varification, username: 'Invalid email' })
        }
        break

      case 'password':
        if (varification.username && data.password.length > 5) {
          setVarification({ ...varification, password: null })

          setIsValid(true)
        } else {
          setIsValid(false)
          setVarification({
            ...varification,
            password: 'Invalid password, might be > 5',
          })
        }
        break

      default:
        break
    }
  }

  function handleCloseModal() {
    setIsModal(false)
  }

  return (
    <div className='login'>
      <h1>Please login</h1>
      <form onSubmit={handleFromSubmit}>
        <input
          type='email'
          onBlur={(e) => handleValid(e, 'username')}
          value={data.username}
          onChange={(e) => handleInputChange(e, 'username')}
          placeholder='Enter email'
        />
        {varification.username ? (
          <span className='login_field-errors'>{varification.username}</span>
        ) : null}
        <input
          type='password'
          value={data.password}
          onBlur={(e) => handleValid(e, 'password')}
          onChange={(e) => handleInputChange(e, 'password')}
          placeholder='Enter password'
        />
        {varification.password ? (
          <span className='login_field-errors'>{varification.password}</span>
        ) : null}
        <button
          type='submit'
          disabled={!isValid}
          className={!isValid ? 'login_btn-disabled' : ''}
        >
          Submit
        </button>
      </form>
      {!resultData ? null : (
        <Modal
          data={resultData}
          onClose={handleCloseModal}
          isVisible={isModal}
        />
      )}
    </div>
  )
}

export default Login
